function prosesNormalisasi() {
    const loadingIndicator = document.getElementById(&#039;loadingIndicator&#039;);
    const alertMessage = document.getElementById(&#039;alertMessage&#039;);
    
    // Show loading
    loadingIndicator.classList.remove(&#039;hidden&#039;);
    alertMessage.classList.add(&#039;hidden&#039;);
    
    fetch(&#039;http://localhost:8080/math/normalisasi_matrik/proses&#039;, {
        method: &#039;POST&#039;,
        headers: {
            &#039;Content-Type&#039;: &#039;application/json&#039;,
            &#039;X-Requested-With&#039;: &#039;XMLHttpRequest&#039;
        }
    })
    .then(response =&gt; response.json())
    .then(data =&gt; {
        loadingIndicator.classList.add(&#039;hidden&#039;);
        
        if (data.success) {
            showAlert(&#039;success&#039;, data.message + ` (${data.data_count} data berhasil diproses)`);
            setTimeout(() =&gt; {
                window.location.reload();
            }, 1500);
        } else {
            showAlert(&#039;error&#039;, data.message);
        }
    })
    .catch(error =&gt; {
        loadingIndicator.classList.add(&#039;hidden&#039;);
        showAlert(&#039;error&#039;, &#039;Terjadi kesalahan: &#039; + error.message);
    });
}

function resetData() {
    if (!confirm(&#039;Apakah Anda yakin ingin menghapus semua data normalisasi?&#039;)) {
        return;
    }
    
    const alertMessage = document.getElementById(&#039;alertMessage&#039;);
    
    fetch(&#039;http://localhost:8080/math/normalisasi_matrik/reset&#039;, {
        method: &#039;POST&#039;,
        headers: {
            &#039;Content-Type&#039;: &#039;application/json&#039;,
            &#039;X-Requested-With&#039;: &#039;XMLHttpRequest&#039;
        }
    })
    .then(response =&gt; response.json())
    .then(data =&gt; {
        if (data.success) {
            showAlert(&#039;success&#039;, data.message);
            setTimeout(() =&gt; {
                window.location.reload();
            }, 1500);
        } else {
            showAlert(&#039;error&#039;, data.message);
        }
    })
    .catch(error =&gt; {
        showAlert(&#039;error&#039;, &#039;Terjadi kesalahan: &#039; + error.message);
    });
}

function showAlert(type, message) {
    const alertMessage = document.getElementById(&#039;alertMessage&#039;);
    const alertClass = type === &#039;success&#039; ? &#039;bg-green-50 border-green-500 text-green-700&#039; : &#039;bg-red-50 border-red-500 text-red-700&#039;;
    const iconPath = type === &#039;success&#039; 
